// Game.js
import styles from './Game.module.css';

function Game({ card, disabled, handleChoice, flipped }) { 
    
    function onCardClick() {
        if (!disabled) handleChoice(card);
    }

    return (
        <div className={styles.card} onClick={onCardClick}>
            <div className={flipped ? styles.flipped : ""}>
                <img
                    className={`${styles.front} ${card.matched ? styles.matched : ''}`}
                    src={card.src}
                    alt="card front"
                />
                <img
                    className={styles.back}
                    src="https://images-wixmp-ed30a86b8c4ca887773594c2.wixmp.com/f/4f7705ec-8c49-4eed-a56e-c21f3985254c/dah43cy-a8e121cb-934a-40f6-97c7-fa2d77130dd5.png/v1/fill/w_759,h_1053/pokemon_card_backside_in_high_resolution_by_atomicmonkeytcg_dah43cy-pre.png?token=eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9..9GzaYS7sd8RPY5FlHca09J9ZQZ9D9zI69Ru-BsbkLDA"
                    alt="card back"
                />
            </div>
        </div>
    );
}

export default Game;

